mapper.update:
UPDATE transporter_request
SET tpr_decision_status = #{decisionStatus}
WHERE id = #{id}

mapper.select:
SELECT tp.transfer_request_id
FROM transporter_request tp
WHERE tp.transfer_request_id = #{transferRequestId}

SELECT *
FROM transporter_request tr
WHERE tr.transporter_id = #{transporterId}

SELECT transporter_id
FROM transporter_request
WHERE id = #{transportRequestId}

SELECT store_name
FROM transporter
WHERE id = #{transporterId};
